sections.code:
body {
    background-color: #222222;
    box-shadow: none;
    scrollbar-width: none;
}

html {
    background: #222222;
}

#tab-course {
    background-color: #c0d5ea;
    background-image: none;
    position: fixed;
}

#tab-course, #tab-semester {
    z-index: 4;
}

#tab-semester li.course.current a.courseLink {
    background: none;
    background-image: none;
    background-color: #c0d5ea;
}

#tab-course a.current {
    background-color: #3c6ba8;
    border: none;
    color: #f5f5f5;
    border-radius: 5px;
    margin: 5px 0;
    height: calc(40% - 4px);
    padding: 4px 8px 12px 8px;
}

#tab-course a.current:hover {
    background-color: #1c4b88;
}

#tab-course a {
    text-shadow: none;
}

body.sidebar.studentView #main .course-group div a,
body.sidebar.studentView #main #courseListTabs li a {
    color: #f2f2f2 !important;
}

#sidenav {
    z-index: 3;
    position: fixed !important;
    overflow-y: scroll;
    scrollbar-width: none;
}

#sidenav h2 {
    color: #f5f5f5;
}

#sidenav.sidenav,
body.studentView.sidebar nav#sidenav.scroll {
    background: transparent;
    color: #f5f5f5;
    background-color: #444444;
    height: 100%;
    position: absolute;
    margin-top: 0px;
}

#sidenav a {
    color: #f5f5f5;
    background-color: #444444;
}

div.sidebarCalendar {
    color: #f5f5f5 !important;
    background: none !important;
    background-image: none !important;
}

#allMiniCalendars table.calendarMonth tbody tr td {
    background-color: #222 !important;
    background: none !important;
    background-image: none !important;
}

#sidenav a.current {
    background: transparent;
    color: #f5f5f5;
    background-color: #444444;
    border: none;
}

body > footer {
    background: #333333;
    border: none;
}

.course a.course-title {
    color: #58B
}

a,
a:link {
    color: #58B;
}

.btn-primary,
a.action2,
a.edit,
div.addDayButton {
    text-shadow: none;
    color: #f5f5f5;
    background-color: #36A;
    background-image: none;
}

.btn,
.basic_button,
a.action,
input.action,
a.action2,
a.edit a.edit2,
#sidenav a.edit,
div.addDayButton,
a.cancel,
a.cancelPopup {
    text-shadow: none;
    background-image: none;
    background-color: #D5D5D5;
}

#main {
    color: #f5f5f5;
}

.greenHeader {
    background: none;
    background-color: transparent !important;
    background-image: none;
    color: #f5f5f5;
}

.dashboardDay .categoryTitle {
    color: #f5f5f5;
    background: none;
    background-color: transparent;
}

#dashboardAnnounce h2,
#dashboardAnnounce p {
    color: #f5f5f5;
}

h2#scheduleTitle {
    color: #58B;
}

.desc h2 {
    color: #58B;
}

nav#sidenav a:hover {
    color: #222222;
    background-color: #888;
    border: none !important;
}

h1#page-title {
    color: #58B;
}

div#page-content.content-page-body p {
    color: #f5f5f5 !important;
}

div#page-content.content-page-body a {
    color: #58B !important;
    text-decoration: none;
    background-color: transparent;
    background: none;
}

#assignmentsTable .category {
    background: none;
    background-image: none;
    background-color: #666 !important;
    border-bottom: 2px solid #AAA;
}

#assignmentsTable th span,
#assignmentsTable th.percent-col,
#assignmentsTable th.stats-col {
    color: #f5f5f5;
}

tbody tr.assignment td.title-col a.viewPrintLink {
    color: #69C;
    font-weight: 500;
}

.view-stats.btn.btn-primary.btn-small {
    color: #3A3A3A;
    background-color: #69C;
}

.view-stats.btn.btn-primary.btn-small:hover {
    color: #BBB;
    background-color: #24A;
}

.submitAssignmentLink.btn.btn-success.btn-small {
    color: #3A3A3A;
}

.submitAssignmentLink.btn.btn-success.btn-small:hover {
    color: #D3D3D3;
}

.collapse-col {
    color: #236;
    font-weight: 700;
}

#assignmentsFooter #calculationOptions {
    color: #f5f5f5;
    background: none;
    background-image: none;
    background-color: #444;
}

#assignmentsFooter .course-header {
    color: #f5f5f5;
    background-color: #444;
}

#assignmentsFooter #totalScoreContainer #totalScoreInfo {
    color: #d5d5d5;
}

#assignmentsFooter #totalScoreContainer .score-header {
    color: #d5d5d5;
}

#assignmentsFooter #totalScoreContainer .weightedCategoryWarning {
    color: #d5d5d5;
}

#assignmentsFooter #currentScoreContainer #currentScoreInfo {
    color: #d5d5d5;
}

.scheduleWeek {
    background: none;
    background-image: none;
    background-color: #777;
}

.scheduleWeek .week-label {
    color: #258;
}

h3#instructorInfo.section_heading_minor {
    color: #58B;
}

h1.section_heading_major.syllabus_heading {
    color: #58B;
}

#calendarTable tbody p {
    color: #f5f5f5;
}

#calendarTable tbody .calendarItem .clickForDesc {
    color: #58B;
}

#calendarViewTitle #timePeriod {
    color: #f5f5f5;
}

#gridView tr .class-day-cell,
.sidebar td {
    background-color: #333;
}

header#aggregateCalendarHeader {
    background-color: #222 !important;
}

header#aggregateCalendarHeader .dateNav h2.date {
    color: #58B;
}

div.sidebarCalendar {
    color: #444;
}

.dashboardAnnouncement .dashboardAnnouncementText span {
    color: #f5f5f5 !important;
    background-color: transparent !important;
}

.dashboardAnnouncement h3 {
    color: #58B;
}

.course-refresh.btn.btn-small {
    background-color: #985bfa !important;
    border: none;
    color: #f5f5f5;
    padding: 5px 16px !important;
}

.course-refresh.btn.btn-small:hover {
    background-color: #784fb0 !important;
}

#sidenav a.current {
    color: #69F;
}

a.btn.btn-link.renderMessageSettings {
    background-color: transparent;
    background: none;
    background-image: none;
}

.announcement .announcementContent .announcementText p span {
    color: #f5f5f5 !important;
    background-color: transparent !important;
}

.announcement .announcementContent .announcementText p a {
    color: #f5f5f5 !important;
    background-color: transparent !important;
}

/*
MESSAGES SECTION
*/
#messagesFullScreenPopup #conversationView header,
#messagesFullScreenPopup #conversationListView header {
    color: #E5E5E5;
    background-color: #222;
}

#messagesFullScreenPopup #conversationView header .conversationInfo {
    color: #f5f5f5;
}

#messagesFullScreenPopup #conversationListView header .searchWrapper,
#messagesFullScreenPopup #conversationListview header .searchWrapper input {
    background-color: #444;
    border: none;
}

#messagesFullScreenPopup #conversationListView #conversationList li .selectConversation.underlay {
    color: #f5f5f5;
    background-color: #333;
}

#messagesFullScreenPopup #conversationListView #conversationList li .selectConversation {
    color: #d5d5d5;
}

#messagesFullScreenPopup #conversationListView #conversationList li .conversationRecipients {
    color: #b1cdff;
}

#messagesFullScreenPopup #conversationListView #conversationList li .courseIndicator {
    color: #fff;
}

ul#messageList {
    background-color: #555 !important;
    background: none !important;
    background-image: none !important;
}

li.message {
    background-color: #5a5a5a !important;
    color: #fff;
}

#messagesFullScreenPopup #conversationView #messageList .message .senderName {
    color: #bce;
}

#messagesFullScreenPopup #conversationView .conversationWrapper {
    background-color: #111;
}

#messagesFullScreenPopup #conversationView #replyInterface {
    color: #fff;
    background-color: #444;
}

#messagesFullScreenPopup #conversationView #replyInterface .btn {
    border: none;
    color: #fff;
    background-color: #666;
}

/*
SETTINGS POPUP
*/
#preferencesPopup {
    background-color: #222 !important;
}

#preferencesPopup .tabContent {
    background-color: #333 !important;
    border: none !important;
}

#preferencesPopup .nav-tabs > .active > a {
    background-color: #333 !important;
}

#preferencesPopup .nav-tabs a {
    color: #f5f5f5;
}

#preferencesPopup .btn {
    background-color: #444;
    border: none;
    color: #f5f5f5;
}

#preferencesPopup #messagePreferences #messageTabNav .messageTab {
    color: #f5f5f5;
}

#preferencesPopup #messagePreferences #messageTabNav .messageTab:hover {
    background-color: #666;
}

#preferencesPopup #messagePreferences #filterContent h3 {
    color: #58B !important;
}

#assignmentDetails.insert.LSPopupControl.LSPopupDialog.ui-draggable {
    background-color: #333 !important;
}

#assignmentDetails.insert.LSPopupControl.LSPopupDialog.ui-draggable .btn {
    background-color: #555 !important;
    color: #f5f5f5;
    border: none;
}

#assignmentDetails.insert.LSPopupControl.LSPopupDialog.ui-draggable .action {
    background-color: #555 !important;
    color: #58B;
    border: none;
}

#assignmentDetails .popupMain h4.subtitle,
#assignmentDetails .popupMain .dateInfo .dueDateText .begin {
    color: #fff !important;
}

/*
EXAM SECTION
*/
section#main .info_container .instructions {
    background-color: #3B405A;
}

section#main .info_container #assessment #beginButton {
    background-color: #444 !important;
    border: none;
}

/*
TEST SECTION
*/
section#studentAssessmentContent {
    background-color: #222 !important;
}

section#studentAssessmentContent nav#studentExamNav {
    background-color: #444 !important;
    box-shadow: 0px 5px 20px black;
}

section#studentAssessmentContent li.questionButton {
    background-color: #dadada;
    border-bottom: 2px solid #222;
    border-radius: 4px !important;
}

section#studentAssessmentContent li.questionButton a {
    background: none !important;
    color: #1c338d;
    font-weight: 600 !important;
}

section#studentAssessmentContent li.questionButton:hover {
    background-color: #999 !important;
}

section#studentAssessmentContent nav#studentExamNav h4 {
    color: #58B;
}

section#studentAssessmentContent nav#studentExamNav .btn {
    background-color: #666;
    border: none;
    color: #f5f5f5;
}

section#studentAssessmentContent .btn:hover {
    background-color: #555 !important;
}

section#studentAssessmentContent .btn.dropdown-toggle {
    background-color: #DDD !important;
    margin: 2px;
}

section#studentAssessmentContent .btn.dropdown-toggle:hover {
    background-color: #AAA !important;
}

section#studentAssessmentContent section.main #studentExamQuestionList {
    background-color: #333 !important;
}

section#studentAssessmentContent section.main #studentExamQuestionList div.question.clearfix {
    color: #6AF;
}

#studentAssessmentContent .main .question.clearfix .questionText {
    color: #fff;
}

#studentAssessmentContent .main ol.optionsList li.answer.answerText {
    color: #f5f5f5 !important;
}


#studentAssessmentContent .main li.answer.answerText::marker {
    color: #6AF;
}



section#studentAssessmentContent section.main #studentExamQuestionList div.question.clearfix .questionRight .btn {
    background-color: #555;
    border: none;
}

section#studentAssessmentContent section.main header.subHeader {
    background-color: #222;
    color: #6AF;
}

section#studentAssessmentContent section.main div.navButtons {
    background-color: #444;
    color: #6AF;
}

section#studentAssessmentContent section.main div.navButtons .btn {
    background-color: #666;
    color: #f5f5f5;
    border: none;
}

nav#studentExamNav div.TimerSection #timer {
    color: #f5f5f5 !important;
}

nav#studentExamNav div.TimerSection #hideButton {
    color: #6AF;
}

section.main {
    margin: 29px 0px 0px 15.6em;
}

#studentExamNav.main {
    min-height: 100%;
    padding: 0 2em 0 2em;
}

nav#studentExamNav {
    position: fixed;
    margin-top: 66px !important;
}

header.main {
    z-index: 4 !important;
    position: fixed !important;
    margin: 0px !important;
    background-image: none !important;
    outline: none;
    border: none;
}

section#studentAssessmentContent {
    max-width: 100vw !important;
    overflow: hidden !important;
}

li.questionButton a[answered="true"] {
    color: #0bb471 !important;
    text-shadow: none !important;
    background-color: #222 !important;
}

.insert,
.insert .title {
    background-color: #333 !important;
    color: #f5f5f5 !important;
}

.cke_editable.cke_editable_themed.cke_contents_ltr p {
